import React, { useState, useEffect } from 'react';
import L from 'leaflet';
import './Map.css'; // Asegúrate de importar el archivo CSS

const Map = () => {
  const [gpsPoints, setGpsPoints] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchGpsPoints = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/gps');
        const data = await response.json();
        setGpsPoints(data);
      } catch (err) {
        setError('Hubo un error al cargar los puntos GPS. Intenta nuevamente.');
      }
    };

    fetchGpsPoints();
  }, []);

  useEffect(() => {
    if (gpsPoints.length > 0) {
      // Crear el mapa después de que se carguen los puntos GPS
      const map = L.map('map').setView([51.505, -0.09], 13); // Establece la vista inicial del mapa

      // Cargar el mapa de OpenStreetMap
      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
      }).addTo(map);

      // Agregar marcadores para cada punto GPS
      gpsPoints.forEach((point) => {
        L.marker([point.latitude, point.longitude]).addTo(map).bindPopup(`<b>ID:</b> ${point.id}`);
      });
    }
  }, [gpsPoints]); // Solo se ejecuta cuando los puntos GPS están disponibles

  return (
    <div className="map-container">
      {error && (
        <div
          style={{
            position: 'absolute',
            top: '20px',
            left: '50%',
            transform: 'translateX(-50%)',
            padding: '10px',
            backgroundColor: '#f44336',
            color: 'white',
            borderRadius: '5px',
          }}
        >
          {error}
        </div>
      )}
      <div id="map" style={{ width: 'calc(100% - 200px)', height: '100vh', margin: '0 100px 100px 100px' }}></div>
    </div>
  );
};

export default Map;
