import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import './Map.css';
import axios from 'axios';

const API_KEY = 'AIzaSyAroIurW0UnnyU7b_n7RQtkxAb3Asmmb2A'; // Clave de Google Geolocation si la necesitas

const Map = () => {
  const [gpsPoints, setGpsPoints] = useState([]);
  const [currentLocation, setCurrentLocation] = useState(null);
  const [loading, setLoading] = useState(true); 
  const [error, setError] = useState(null);

  useEffect(() => {
    // Obtener puntos GPS desde el servidor
    axios.get('http://localhost:5000/api/gps')
      .then(response => {
        setGpsPoints(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error("Error al obtener los puntos GPS:", error);
        setError('Error al cargar los puntos GPS.');
        setLoading(false);
      });

    // Obtener ubicación del navegador (usando geolocalización)
    const fetchLocation = () => {
      if ("geolocation" in navigator) {
        navigator.geolocation.getCurrentPosition(
          (position) => {
            const { latitude, longitude } = position.coords;
            setCurrentLocation({ latitude, longitude });

            // Guardar punto GPS en el servidor
            const sent_at = new Date().toISOString();
            const phone_identifier = "telefono-123";

            axios.post('http://localhost:5000/api/gps', {
              latitude,
              longitude,
              sent_at,
              phone_identifier
            })
            .then(() => {
              console.log('Punto GPS guardado');
            })
            .catch(error => {
              console.error("Error al guardar el punto GPS:", error);
            });
          },
          (error) => {
            console.error("Error al obtener la ubicación:", error);
            setError('No se pudo obtener la ubicación del dispositivo.');
          }
        );
      } else {
        console.error("La geolocalización no está disponible en este navegador.");
        setError('La geolocalización no está disponible en este navegador.');
      }
    };

    fetchLocation(); // Llamar a la función para obtener la ubicación

  }, []);

  const customIcon = new L.Icon({
    iconUrl: 'data:image/svg+xml;charset=UTF-8,' + encodeURIComponent(`
      <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="24" height="24">
        <circle cx="12" cy="5" r="3" fill="#ff6347" />
        <path d="M12 0c-4.41 0-8 3.59-8 8 0 4.89 6 12 6 12s2-3.3 4-6.6c2 3.3 4 6.6 4 6.6s6-7.11 6-12c0-4.41-3.59-8-8-8z" fill="#ff6347"/>
      </svg>
    `),
    iconSize: [30, 40],
    iconAnchor: [15, 40],
    popupAnchor: [0, -40],
  });

  if (loading) {
    return <div className="loading">Cargando el mapa...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="map-container">
      <MapContainer
        center={currentLocation ? [currentLocation.latitude, currentLocation.longitude] : [-34.6037, -58.3816]} // Coordenadas por defecto (Buenos Aires)
        zoom={13}
        style={{ width: '100%', height: '100vh' }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        />

        {gpsPoints.map((point) => (
          <Marker
            key={point.id}
            position={[point.latitude, point.longitude]}
            icon={customIcon}
          >
            <Popup>
              <strong>Teléfono:</strong> {point.phone_identifier} <br />
              <strong>Hora de envío:</strong> {new Date(point.sent_at).toLocaleString()}
            </Popup>
          </Marker>
        ))}

        {currentLocation && (
          <Marker position={[currentLocation.latitude, currentLocation.longitude]} icon={customIcon}>
            <Popup>Tu ubicación actual</Popup>
          </Marker>
        )}
      </MapContainer>
    </div>
  );
};

export default Map;
