import React, { useState, useEffect } from 'react';
import { GoogleMap, LoadScript, Marker, InfoWindow } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '400px',
};

const center = {
  lat: -33.8688, // Coordenada predeterminada (por ejemplo, Sydney)
  lng: 151.2093, // Coordenada predeterminada
};

const Map = () => {
  const [userLocation, setUserLocation] = useState(null);
  const [mapLoaded, setMapLoaded] = useState(false);

  // Función para obtener la ubicación del usuario
  const getUserLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setUserLocation({
            lat: position.coords.latitude,
            lng: position.coords.longitude,
          });
        },
        (error) => {
          console.error("Error obteniendo la ubicación: ", error);
        }
      );
    }
  };

  useEffect(() => {
    // Detectamos si el dispositivo es móvil
    const isMobile = /Mobi|Android|iPhone|iPad|iPod/i.test(navigator.userAgent);
    
    // Si es un dispositivo móvil, obtenemos la geolocalización
    if (isMobile) {
      // Obtener la ubicación inicial
      getUserLocation();

      // Actualizar la ubicación cada 5 segundos
      const intervalId = setInterval(() => {
        getUserLocation();
      }, 5000); // 5000ms = 5 segundos

      // Limpiar el intervalo cuando el componente se desmonte
      return () => clearInterval(intervalId);
    } else {
      console.log("No es un dispositivo móvil o geolocalización no soportada");
    }
  }, []);

  const handleMapLoad = () => {
    setMapLoaded(true);
  };

  return (
    <LoadScript googleMapsApiKey="AIzaSyAroIurW0UnnyU7b_n7RQtkxAb3Asmmb2A">
      <GoogleMap
        id="map"
        mapContainerStyle={containerStyle}
        center={userLocation || center} // Centrar el mapa en la ubicación del usuario o en la ubicación predeterminada
        zoom={15}
        onLoad={handleMapLoad}
      >
        {userLocation && (
          <Marker position={userLocation}>
            <InfoWindow position={userLocation}>
              <div>
                <h2>Ubicación actual</h2>
                <p>Latitud: {userLocation.lat}</p>
                <p>Longitud: {userLocation.lng}</p>
              </div>
            </InfoWindow>
          </Marker>
        )}
      </GoogleMap>
    </LoadScript>
  );
};

export default Map;
