import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import './Map.css';
import axios from 'axios';

const Map = () => {
  const [gpsPoints, setGpsPoints] = useState([]);

  useEffect(() => {
    // Hacer la solicitud para obtener los puntos GPS desde el servidor
    axios.get('http://localhost:5000/api/gps')
      .then(response => {
        // Verifica la estructura de los datos y actualiza el estado
        setGpsPoints(response.data);  // Asegúrate de que los datos estén en el formato adecuado
      })
      .catch(error => {
        console.error("Error al obtener los puntos GPS:", error);
      });
  }, []);  // Solo se ejecuta cuando el componente se monta

  // Ícono personalizado para el marcador (pin clásico)
  const customIcon = new L.Icon({
    iconUrl: 'data:image/svg+xml;charset=UTF-8,' + encodeURIComponent(`
      <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="24" height="24">
        <circle cx="12" cy="5" r="3" fill="#ff6347" />
        <path d="M12 0c-4.41 0-8 3.59-8 8 0 4.89 6 12 6 12s2-3.3 4-6.6c2 3.3 4 6.6 4 6.6s6-7.11 6-12c0-4.41-3.59-8-8-8z" fill="#ff6347"/>
      </svg>
    `),
    iconSize: [30, 40], // Tamaño del ícono
    iconAnchor: [15, 40], // Centro del ícono en el punto
    popupAnchor: [0, -40], // Ajuste del popup (aparece encima del pin)
  });

  return (
    <div className="map-container">
      <MapContainer
        center={[-34.6037, -58.3816]} // Coordenadas de Buenos Aires
        zoom={13}
        style={{ width: '100%', height: '100vh' }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        />

        {/* Mapeo de puntos GPS recibidos */}
        {gpsPoints.map((point) => (
          <Marker
            key={point.id}
            position={[point.latitude, point.longitude]}  // Coordenadas de cada punto
            icon={customIcon}  // Icono personalizado
          >
            <Popup>
              {/* Mostrar información adicional sobre el punto */}
              <strong>Teléfono:</strong> {point.phone_identifier} <br />
              <strong>Hora de envío:</strong> {new Date(point.sent_at).toLocaleString()}
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
};

export default Map;
