import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import './Map.css';
import axios from 'axios';

const API_KEY = 'AIzaSyAroIurW0UnnyU7b_n7RQtkxAb3Asmmb2A'; // Clave de Google Geolocation si la necesitas

const Map = () => {
    const [currentLocation, setCurrentLocation] = useState(null);
    const [error, setError] = useState(null);
  
    useEffect(() => {
      // Intenta obtener la ubicación usando la geolocalización del navegador
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          (position) => {
            setCurrentLocation({
              latitude: position.coords.latitude,
              longitude: position.coords.longitude,
            });
          },
          (error) => {
            switch (error.code) {
              case error.PERMISSION_DENIED:
                setError('El usuario ha denegado el acceso a la ubicación.');
                break;
              case error.POSITION_UNAVAILABLE:
                setError('No se pudo obtener la ubicación.');
                break;
              case error.TIMEOUT:
                setError('La solicitud de geolocalización ha superado el tiempo de espera.');
                break;
              default:
                setError('Error desconocido al obtener la ubicación.');
                break;
            }
          }
        );
      } else {
        setError('La geolocalización no es compatible con este navegador.');
      }
  
      // Si la geolocalización no está disponible, intenta con la API de Google
      if (!navigator.geolocation) {
        axios
          .get(`https://www.googleapis.com/geolocation/v1/geolocate?key=${API_KEY}`)
          .then((response) => {
            const { lat, lng } = response.data.location;
            setCurrentLocation({ latitude: lat, longitude: lng });
          })
          .catch((error) => {
            setError('No se pudo obtener la ubicación usando la API de Google.');
            console.error(error);
          });
      }
    }, []);
  
    return (
      <div>
        {error && <div className="error">{error}</div>}
        {currentLocation ? (
          <div>
            <p>Latitud: {currentLocation.latitude}</p>
            <p>Longitud: {currentLocation.longitude}</p>
          </div>
        ) : (
          <div>Obteniendo ubicación...</div>
        )}
      </div>
    );
  };
  
  export default Map;
