import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import './Map.css';
import axios from 'axios';

const Map = () => {
  const [gpsPoints, setGpsPoints] = useState([]);
  const [userLocation, setUserLocation] = useState(null);

  useEffect(() => {
    // Verificar si la geolocalización está disponible en el navegador
    if (navigator.geolocation) {
      const watchId = navigator.geolocation.watchPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setUserLocation({ latitude, longitude });

          // Enviar la ubicación al servidor para guardarla
          axios.post('http://localhost:5000/api/gps', {
            latitude: latitude,
            longitude: longitude,
            sent_at: new Date().toISOString(),  // Hora actual
            vehicle_identifier: "YOUR_VEHICLE_ID"  // Aquí puedes poner el identificador del vehículo o teléfono
          })
          .then(response => {
            console.log("Ubicación guardada:", response.data);
          })
          .catch(error => {
            console.error("Error al guardar la ubicación:", error);
          });
        },
        (error) => {
          console.error("Error de geolocalización:", error);
        },
        {
          enableHighAccuracy: true,
          timeout: 10000,
          maximumAge: 0
        }
      );

      // Limpiar el watch cuando el componente se desmonte para evitar fugas de memoria
      return () => {
        navigator.geolocation.clearWatch(watchId);
      };
    } else {
      console.error("Geolocalización no es compatible con este navegador.");
    }

    // Hacer la solicitud para obtener los puntos GPS desde el servidor
    axios.get('http://localhost:5000/api/gps')
      .then(response => {
        setGpsPoints(response.data);
      })
      .catch(error => {
        console.error("Error al obtener los puntos GPS:", error);
      });
  }, []);  // Solo se ejecuta una vez cuando el componente se monta

  const customIcon = new L.Icon({
    iconUrl: 'data:image/svg+xml;charset=UTF-8,' + encodeURIComponent(`
      <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="24" height="24">
        <circle cx="12" cy="5" r="3" fill="#ff6347" />
        <path d="M12 0c-4.41 0-8 3.59-8 8 0 4.89 6 12 6 12s2-3.3 4-6.6c2 3.3 4 6.6 4 6.6s6-7.11 6-12c0-4.41-3.59-8-8-8z" fill="#ff6347"/>
      </svg>
    `),
    iconSize: [30, 40],
    iconAnchor: [15, 40],
    popupAnchor: [0, -40],
  });

  return (
    <div className="map-container">
      <MapContainer
        center={userLocation ? [userLocation.latitude, userLocation.longitude] : [-34.6037, -58.3816]}
        zoom={13}
        style={{ width: '100%', height: '100vh' }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        />

        {userLocation && (
          <Marker
            position={[userLocation.latitude, userLocation.longitude]}
            icon={customIcon}
          >
            <Popup>
              <strong>Ubicación actual:</strong><br />
              Latitud: {userLocation.latitude}<br />
              Longitud: {userLocation.longitude}
            </Popup>
          </Marker>
        )}

        {gpsPoints.map((point) => (
          <Marker
            key={point.id}
            position={[point.latitude, point.longitude]}
            icon={customIcon}
          >
            <Popup>
              <strong>Teléfono:</strong> {point.phone_identifier} <br />
              <strong>Hora de envío:</strong> {new Date(point.sent_at).toLocaleString()}
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
};

export default Map;
