import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import './Map.css'; // Importa tu CSS para personalización de márgenes, etc.

const Map = () => {
  const [gpsPoints, setGpsPoints] = useState([]);

  useEffect(() => {
    // Ejemplo de datos de GPS, incluyendo números de teléfono
    const gpsPoints = [
      { id: 1, latitude: -33.4489, longitude: -70.6693, phone: '123-456-789' },
      { id: 2, latitude: -33.4500, longitude: -70.6700, phone: '987-654-321' },
    ];
    setGpsPoints(gpsPoints);
  }, []);

  // Icono personalizado para el marcador
  const customIcon = new L.Icon({
    iconUrl: 'https://upload.wikimedia.org/wikipedia/commons/thumb/e/eb/Phone_icon_2.svg/1024px-Phone_icon_2.svg.png', // Puedes usar tu propio ícono aquí
    iconSize: [40, 40],
    iconAnchor: [20, 40], // Centro del ícono
    popupAnchor: [0, -40], // Donde aparecerá el popup
  });

  return (
    <div className="map-container">
      <MapContainer
        center={[-33.4489, -70.6693]} // Coordenadas de Santiago
        zoom={13}
        style={{ width: '100%', height: '100vh' }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        />

        {gpsPoints.map((point) => (
          <Marker
            key={point.id}
            position={[point.latitude, point.longitude]}
            icon={customIcon}
          >
            <Popup>
              <strong>Teléfono:</strong> {point.phone}
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
};

export default Map;
