import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import axios from 'axios';
import './Map.css';

const Map = () => {
  const [gpsPoints, setGpsPoints] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchGpsPoints = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/gps');
        setGpsPoints(response.data);  // Establecer los puntos GPS en el estado
      } catch (err) {
        setError('Error al cargar puntos GPS');
      }
    };

    fetchGpsPoints();
  }, []);

  return (
    <div style={{ height: '100vh', width: '100%' }}>
      {error && <div style={{ color: 'red' }}>{error}</div>}
      <MapContainer center={[51.505, -0.09]} zoom={13} style={{ height: '100%', width: '100%' }}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        {gpsPoints.map((point) => (
          <Marker
            key={point.id}
            position={[point.latitude, point.longitude]}  // Utiliza las coordenadas correctas
            icon={new L.Icon({
              iconUrl: 'https://example.com/phone-icon.png',  // Asegúrate de tener una URL válida para el icono
              iconSize: [32, 32]
            })}
          >
            <Popup>
              <strong>Ubicación:</strong> ({point.latitude}, {point.longitude})<br />
              <strong>Hora:</strong> {new Date(point.sent_at).toLocaleString()}
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
};

export default Map;
