import React, { useState, useEffect, useRef, useLayoutEffect } from 'react';
import L from 'leaflet';
import './Map.css'; // Asegúrate de que este archivo CSS esté bien importado

const Map = () => {
  const [gpsPoints, setGpsPoints] = useState([]);
  const [error, setError] = useState('');
  const mapRef = useRef(null);

  useEffect(() => {
    const fetchGpsPoints = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/gps');
        const data = await response.json();
        setGpsPoints(data);
      } catch (err) {
        setError('Hubo un error al cargar los puntos GPS. Intenta nuevamente.');
      }
    };

    fetchGpsPoints();
  }, []);

  useLayoutEffect(() => {
    if (gpsPoints.length > 0 && mapRef.current && !mapRef.current._leaflet_id) {
      const map = L.map(mapRef.current).setView([51.505, -0.09], 13);

      // Cargar los tiles de OpenStreetMap
      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
      }).addTo(map);

      // Agregar los marcadores
      gpsPoints.forEach((point) => {
        L.marker([point.latitude, point.longitude]).addTo(map).bindPopup(`<b>ID:</b> ${point.id}`);
      });

      // Ajustar el mapa cuando el tamaño cambie
      map.invalidateSize(); // Redibuja el mapa si el tamaño cambia

      // Devolver el mapa para liberar recursos en caso de que el componente se desmonte
      return () => map.remove();
    }
  }, [gpsPoints]);

  return (
    <div className="map-container">
      {error && (
        <div
          style={{
            position: 'absolute',
            top: '20px',
            left: '50%',
            transform: 'translateX(-50%)',
            padding: '10px',
            backgroundColor: '#f44336',
            color: 'white',
            borderRadius: '5px',
          }}
        >
          {error}
        </div>
      )}
      <div
        ref={mapRef}
        style={{
          width: '100%',  // El mapa ocupará todo el contenedor
          height: '100%', // El mapa ocupará el 100% de la altura disponible
        }}
      />
    </div>
  );
};

export default Map;
