import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import axios from 'axios';
import './Map.css';

const Map = () => {
  const [gpsPoints, setGpsPoints] = useState([]); // Hook de estado para los puntos GPS
  const [error, setError] = useState(''); // Hook de error

  // Uso de useEffect para cargar los puntos GPS desde la API
  useEffect(() => {
    const fetchGpsPoints = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/gps');
        setGpsPoints(response.data);  // Establecer los puntos GPS en el estado
      } catch (err) {
        setError('Hubo un error al cargar los puntos GPS. Intenta nuevamente.');
      }
    };

    fetchGpsPoints();
  }, []);  // Se ejecuta una sola vez cuando el componente se monta

  return (
    <div style={{ height: '100vh', width: '100%' }}>
      {error && (
        <div style={{
          position: 'absolute', top: '20px', left: '50%', transform: 'translateX(-50%)',
          padding: '10px', backgroundColor: '#f44336', color: 'white', borderRadius: '5px'
        }}>
          {error}
        </div>
      )}
      <MapContainer center={[51.505, -0.09]} zoom={13} style={{ height: '100%', width: '100%' }}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        {gpsPoints.length > 0 ? (
          gpsPoints.map((point) => (
            <Marker
              key={point.id}
              position={[point.latitude, point.longitude]}
              icon={new L.Icon({
                iconUrl: 'https://example.com/phone-icon.png',  // Cambia a tu URL de icono personalizado
                iconSize: [32, 32],
                iconAnchor: [16, 32],
                popupAnchor: [0, -32],
              })}
            >
              <Popup>
                <strong>Ubicaci√≥n:</strong> ({point.latitude}, {point.longitude})<br />
                <strong>Hora:</strong> {new Date(point.sent_at).toLocaleString()}
              </Popup>
            </Marker>
          ))
        ) : (
          <div style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', color: 'gray' }}>
            No hay puntos GPS disponibles.
          </div>
        )}
      </MapContainer>
    </div>
  );
};

export default Map;
