import React, { useState, useEffect, useRef } from 'react';
import L from 'leaflet'; // Importar Leaflet
import './Map.css'; // Asegúrate de importar el CSS correctamente

const Map = () => {
  const [gpsPoints, setGpsPoints] = useState([]);
  const [error, setError] = useState('');
  const mapRef = useRef(null); // Referencia al contenedor del mapa
  const mapInstance = useRef(null); // Referencia a la instancia del mapa

  // Fetch de los puntos GPS
  useEffect(() => {
    const fetchGpsPoints = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/gps');
        const data = await response.json();
        setGpsPoints(data);
      } catch (err) {
        setError('Hubo un error al cargar los puntos GPS. Intenta nuevamente.');
      }
    };

    fetchGpsPoints();
  }, []);

  useEffect(() => {
    if (gpsPoints.length > 0 && mapRef.current && !mapInstance.current) {
      // Inicializar el mapa solo una vez
      mapInstance.current = L.map(mapRef.current).setView([51.505, -0.09], 13);

      // Agregar capa de tiles de OpenStreetMap
      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
      }).addTo(mapInstance.current);

      // Agregar marcadores
      gpsPoints.forEach((point) => {
        L.marker([point.latitude, point.longitude]).addTo(mapInstance.current).bindPopup(`<b>ID:</b> ${point.id}`);
      });

      // Ajustar el tamaño del mapa al cambiar el tamaño de la ventana
      mapInstance.current.invalidateSize();
    }

    // Limpiar el mapa si el componente se desmonta
    return () => {
      if (mapInstance.current) {
        mapInstance.current.remove();
        mapInstance.current = null;
      }
    };
  }, [gpsPoints]); // Solo se ejecuta cuando gpsPoints cambia

  return (
    <div className="map-container">
      {error && (
        <div
          style={{
            position: 'absolute',
            top: '20px',
            left: '50%',
            transform: 'translateX(-50%)',
            padding: '10px',
            backgroundColor: '#f44336',
            color: 'white',
            borderRadius: '5px',
          }}
        >
          {error}
        </div>
      )}
      <div
        ref={mapRef}
        style={{
          width: '100%',  // El mapa ocupará todo el contenedor
          height: '100%', // El mapa ocupará el 100% de la altura disponible
        }}
      />
    </div>
  );
};

export default Map;
