import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import './Map.css'; 
import axios from 'axios';

const Map = () => {
  const [gpsPoints, setGpsPoints] = useState([]);

  useEffect(() => {
    // Hacer la solicitud para obtener los puntos GPS desde el servidor
    axios.get('http://localhost:5000/api/gps')
      .then(response => {
        // Actualizar el estado con los datos de los puntos GPS
        setGpsPoints(response.data);
      })
      .catch(error => {
        console.error("Error al obtener los puntos GPS:", error);
      });
  }, []);  // Se ejecuta solo una vez, cuando el componente se monta

  const customIcon = new L.Icon({
    iconUrl: 'https://upload.wikimedia.org/wikipedia/commons/a/a7/Map_pin_icon.svg', // Puedes usar cualquier imagen o URL de ícono aquí
    iconSize: [30, 40], // Tamaño del ícono
    iconAnchor: [15, 40], // Centro del ícono en el punto
    popupAnchor: [0, -40], // Ajuste del popup (aparece encima del pin)
  });

  return (
    <div className="map-container">
      <MapContainer
        center={[-33.4489, -70.6693]} // Coordenadas de Santiago
        zoom={13}
        style={{ width: '100%', height: '100vh' }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        />

        {gpsPoints.map((point) => (
          <Marker
            key={point.id}
            position={[point.latitude, point.longitude]}
            icon={customIcon}
          >
            <Popup>
              <strong>Teléfono:</strong> {point.phone_identifier} <br />
              <strong>Hora de envío:</strong> {new Date(point.sent_at).toLocaleString()}
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
};

export default Map;
