import React, { useState, useEffect } from 'react';
import { GoogleMap, LoadScript, Marker, InfoWindow } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '400px',
};

const Map = () => {
  const [userLocation, setUserLocation] = useState(null);
  const [mapLoaded, setMapLoaded] = useState(false);

  // Función para obtener la ubicación del usuario
  const getUserLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setUserLocation({ lat: latitude, lng: longitude });
        },
        (error) => {
          console.error("Error obteniendo la ubicación: ", error);
        }
      );
    } else {
      console.log("La geolocalización no está soportada en este navegador.");
    }
  };

  useEffect(() => {
    // Detectar si es un dispositivo móvil
    const isMobile = /Mobi|Android|iPhone|iPad|iPod/i.test(navigator.userAgent);
    
    // Si es un dispositivo móvil, obtenemos la geolocalización
    if (isMobile) {
      getUserLocation();  // Obtener la ubicación inicial del usuario cuando el componente se monta
    } else {
      console.log('No es un dispositivo móvil.');
    }
  }, []);

  const handleMapLoad = () => {
    setMapLoaded(true);
  };

  return (
    <LoadScript googleMapsApiKey="AIzaSyAroIurW0UnnyU7b_n7RQtkxAb3Asmmb2A">
      <GoogleMap
        id="map"
        mapContainerStyle={containerStyle}
        center={userLocation || { lat: -33.8688, lng: 151.2093 }}  // Si no hay ubicación, centramos el mapa en una ubicación por defecto
        zoom={15}
        onLoad={handleMapLoad}
      >
        {userLocation && (
          <Marker position={userLocation}>
            <InfoWindow position={userLocation}>
              <div>
                <h2>Ubicación actual</h2>
                <p>Latitud: {userLocation.lat}</p>
                <p>Longitud: {userLocation.lng}</p>
              </div>
            </InfoWindow>
          </Marker>
        )}
      </GoogleMap>
    </LoadScript>
  );
};

export default Map;
