import React, { useState, useEffect } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';

const Map = () => {
  const [gpsPoints, setGpsPoints] = useState([]);
  const [error, setError] = useState('');

  // Cargar los puntos GPS desde la API
  useEffect(() => {
    const fetchGpsPoints = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/gps');
        const data = await response.json();
        setGpsPoints(data);
      } catch (err) {
        setError('Hubo un error al cargar los puntos GPS. Intenta nuevamente.');
      }
    };

    fetchGpsPoints();
  }, []);

  const containerStyle = {
    width: '100%',
    height: '100vh',
  };

  const center = {
    lat: 51.505,
    lng: -0.09,
  };

  return (
    <div>
      {error && (
        <div style={{ position: 'absolute', top: '20px', left: '50%', transform: 'translateX(-50%)', padding: '10px', backgroundColor: '#f44336', color: 'white', borderRadius: '5px' }}>
          {error}
        </div>
      )}
      
      <LoadScript googleMapsApiKey="YOUR_GOOGLE_MAPS_API_KEY">
        <GoogleMap
          mapContainerStyle={containerStyle}
          center={center}
          zoom={13}
        >
          {gpsPoints.length > 0 ? (
            gpsPoints.map((point) => (
              <Marker
                key={point.id}
                position={{
                  lat: point.latitude,
                  lng: point.longitude,
                }}
              />
            ))
          ) : (
            <div style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', color: 'gray' }}>
              No hay puntos GPS disponibles.
            </div>
          )}
        </GoogleMap>
      </LoadScript>
    </div>
  );
};

export default Map;
